CREATE
DEFINER = 'evento'@'%'
PROCEDURE sp_clientes_crear (IN p_nombre_cliente varchar(255),
IN p_direccion_cliente varchar(255),
IN p_telefono_cliente varchar(20),
IN p_website_cliente varchar(100),
IN p_id_tipo_documento int,
IN p_numero_documento varchar(20),
IN p_id_ubigeo int,
IN p_correo_electronico varchar(100),
IN p_observaciones text)
BEGIN
  DECLARE v_existe int DEFAULT 0;

  SELECT
    COUNT(*) INTO v_existe
  FROM clientes
  WHERE numero_documento COLLATE utf8mb4_unicode_ci = p_numero_documento COLLATE utf8mb4_unicode_ci;

  IF v_existe > 0 THEN
    SELECT
      0 AS resultado,
      'Nro de Documento ya se encuentra registrado.' AS mensaje;
  ELSE
    id_tipo_documento, numero_documento, id_ubigeo, correo_electronico, observaciones)
      VALUES (p_nombre_cliente, p_direccion_cliente, p_telefono_cliente, p_website_cliente, p_id_tipo_documento, TRIM(p_numero_documento), p_id_ubigeo, p_correo_electronico, p_observaciones);

    SELECT
      1 AS resultado,
      'Cliente registrado correctamente.' AS mensaje;
  END IF;
END
$$

--
-- Create procedure `sp_clientes_actualizar`
--
CREATE
DEFINER = 'evento'@'%'
PROCEDURE sp_clientes_actualizar (IN p_id_cliente int,
IN p_nombre_cliente varchar(255),
IN p_direccion_cliente varchar(255),
IN p_telefono_cliente varchar(20),
IN p_website_cliente varchar(100),
IN p_id_tipo_documento int,
IN p_numero_documento varchar(20),
IN p_id_ubigeo int,
IN p_correo_electronico varchar(100),
IN p_observaciones text)
BEGIN
  DECLARE v_id_existente int;

  -- Buscar si existe el mismo número de documento en otro cliente
  SELECT
    id_cliente INTO v_id_existente
  FROM clientes
  WHERE TRIM(numero_documento) COLLATE utf8mb4_unicode_ci = TRIM(p_numero_documento) COLLATE utf8mb4_unicode_ci
  LIMIT 1;

  IF v_id_existente IS NOT NULL
    AND v_id_existente <> p_id_cliente THEN
    SELECT
      0 AS resultado,
      'Nro de Documento ya se encuentra registrado en otro cliente.' AS mensaje;
  ELSE
    UPDATE clientes
    SET nombre_cliente = p_nombre_cliente,
        direccion_cliente = p_direccion_cliente,
