  IF p_mes = 0 THEN
    SET p_mes = NULL;
  END IF;
  IF p_id_usuario = 0 THEN
    SET p_id_usuario = NULL;
  END IF;

  SELECT
    MONTH(o.fecha_cierre) AS mes,
    SUM(o.valor_estimado) AS total_perdido
  FROM oportunidades o
  WHERE YEAR(o.fecha_cierre) = p_anio
  AND o.etapa = 'Perdida'
  AND (p_mes IS NULL
  OR MONTH(o.fecha_cierre) = p_mes)
  AND (p_id_usuario IS NULL
  OR o.usuario_creacion_id = p_id_usuario)
  GROUP BY MONTH(o.fecha_cierre)
  ORDER BY MONTH(o.fecha_cierre);
END
$$

