DEFINER = 'evento'@'%'
PROCEDURE sp_dashboard_estadisticas ()
BEGIN
  SELECT
    (SELECT
        COUNT(*)
      FROM clientes
      WHERE estado = 1) AS total_clientes,
    (SELECT
        COUNT(*)
      FROM oportunidades
      WHERE estado = 1
      AND etapa NOT IN ('Ganada', 'Perdida')) AS oportunidades_activas,
    (SELECT
        COUNT(*)
      FROM actividades
      WHERE fecha_actividad >= CURDATE()) AS actividades_pendientes;
END
$$

--
-- Create procedure `sp_actividades_pendientes`
--
CREATE
DEFINER = 'evento'@'%'
PROCEDURE sp_actividades_pendientes ()
BEGIN
  SELECT
    a.id_actividad,
    a.asunto,
    a.descripcion,
    DATE_FORMAT(a.fecha_actividad, '%Y-%m-%d %r') AS fecha_actividad,
    a.tipo_actividad,
    a.id_cliente,
    c.nombre_cliente
  FROM actividades a
    LEFT JOIN clientes c
      ON a.id_cliente = c.id_cliente
  WHERE a.fecha_actividad >= CURDATE();
END
$$

--
-- Create procedure `sp_actividades_listar_por_usuario`
--
