DEFINER = 'evento'@'%'
PROCEDURE sp_actividades_listar_por_usuario (IN in_id_usuario int)
BEGIN

  SELECT
    a.id_actividad,
    a.asunto,
    a.descripcion,
    a.tipo_actividad,
    a.fecha_actividad,
    a.id_cliente,
    c.nombre_cliente,
    a.id_usuario,
    u.nombre_usuario,
    u.id_perfil
  FROM actividades a
    INNER JOIN usuarios u
      ON a.id_usuario = u.id_usuario
    LEFT JOIN clientes c
      ON a.id_cliente = c.id_cliente
  WHERE (in_id_usuario IS NULL
  OR a.id_usuario = in_id_usuario)
  ORDER BY a.fecha_actividad DESC;
END
