--
-- Create procedure `sp_actividades_crear`
--
CREATE
DEFINER = 'evento'@'%'
PROCEDURE sp_actividades_crear (IN p_id_cliente int,
IN p_id_contacto int,
IN p_id_oportunidad int,
IN p_id_usuario int,
IN p_tipo_actividad varchar(100),
IN p_asunto varchar(255),
IN p_descripcion text,
IN p_fecha_actividad datetime)
BEGIN
  DECLARE v_existe int DEFAULT 0;

  SELECT
    COUNT(*) INTO v_existe
  FROM actividades
  WHERE id_cliente = p_id_cliente
  AND fecha_actividad = p_fecha_actividad;

  IF v_existe > 0 THEN
    SELECT
      0 AS resultado,
      'Ya existe una actividad registrada en esa misma hora.' AS mensaje;
  ELSE
    tipo_actividad, asunto, descripcion, fecha_actividad)
      VALUES (p_id_cliente, p_id_contacto, p_id_oportunidad, p_id_usuario, p_tipo_actividad, p_asunto, p_descripcion, p_fecha_actividad);

    SELECT
      1 AS resultado,
      'Actividad registrada correctamente.' AS mensaje;
  END IF;
END
$$

